import { useState, useEffect } from 'react';
import { DownloadIcon } from '@heroicons/react/outline';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import api from '../../services/api';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export default function SalesReport() {
  const [dateRange, setDateRange] = useState('month');
  const [salesData, setSalesData] = useState({
    totalSales: 0,
    totalInvoices: 0,
    averageInvoiceValue: 0,
    pendingPayments: 0,
    dailySales: []
  });

  useEffect(() => {
    fetchSalesData();
  }, [dateRange]);

  const fetchSalesData = async () => {
    try {
      const response = await api.get(`/reports/sales?range=${dateRange}`);
      setSalesData(response.data);
    } catch (err) {
      console.error('Error fetching sales data:', err);
    }
  };

  const chartData = {
    labels: salesData.dailySales.map(sale => sale.date),
    datasets: [
      {
        label: 'Daily Sales',
        data: salesData.dailySales.map(sale => sale.amount),
        fill: false,
        borderColor: 'rgb(79, 70, 229)',
        tension: 0.1
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top'
      },
      title: {
        display: true,
        text: 'Sales Trend'
      }
    },
    scales: {
      y: {
        beginAtZero: true
      }
    }
  };

  const exportReport = () => {
    // Implementation for exporting report to Excel/CSV
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div className="flex space-x-4">
          <select
            value={dateRange}
            onChange={(e) => setDateRange(e.target.value)}
            className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
          >
            <option value="week">Last Week</option>
            <option value="month">Last Month</option>
            <option value="year">Last Year</option>
          </select>
        </div>
        <button
          onClick={exportReport}
          className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700"
        >
          <DownloadIcon className="-ml-1 mr-2 h-5 w-5" />
          Export Report
        </button>
      </div>

      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <dt className="text-sm font-medium text-gray-500 truncate">
              Total Sales
            </dt>
            <dd className="mt-1 text-3xl font-semibold text-gray-900">
              ₹{salesData.totalSales.toFixed(2)}
            </dd>
          </div>
        </div>

        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <dt className="text-sm font-medium text-gray-500 truncate">
              Total Invoices
            </dt>
            <dd className="mt-1 text-3xl font-semibold text-gray-900">
              {salesData.totalInvoices}
            </dd>
          </div>
        </div>

        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <dt className="text-sm font-medium text-gray-500 truncate">
              Average Invoice Value
            </dt>
            <dd className="mt-1 text-3xl font-semibold text-gray-900">
              ₹{salesData.averageInvoiceValue.toFixed(2)}
            </dd>
          </div>
        </div>

        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <dt className="text-sm font-medium text-gray-500 truncate">
              Pending Payments
            </dt>
            <dd className="mt-1 text-3xl font-semibold text-gray-900">
              ₹{salesData.pendingPayments.toFixed(2)}
            </dd>
          </div>
        </div>
      </div>

      <div className="bg-white p-6 shadow rounded-lg">
        <Line data={chartData} options={chartOptions} />
      </div>
    </div>
  );
}
