import { useState, useEffect } from 'react';
import { DownloadIcon } from '@heroicons/react/outline';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import api from '../../services/api';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export default function InventoryReport() {
  const [inventoryData, setInventoryData] = useState({
    totalValue: 0,
    totalItems: 0,
    lowStockItems: [],
    categoryBreakdown: []
  });

  useEffect(() => {
    fetchInventoryData();
  }, []);

  const fetchInventoryData = async () => {
    try {
      const response = await api.get('/reports/inventory');
      setInventoryData(response.data);
    } catch (err) {
      console.error('Error fetching inventory data:', err);
    }
  };

  const chartData = {
    labels: inventoryData.categoryBreakdown.map(item => item.category),
    datasets: [
      {
        label: 'Stock Value by Category',
        data: inventoryData.categoryBreakdown.map(item => item.value),
        backgroundColor: 'rgba(79, 70, 229, 0.5)'
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top'
      },
      title: {
        display: true,
        text: 'Inventory Value by Category'
      }
    }
  };

  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-3">
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <dt className="text-sm font-medium text-gray-500 truncate">
              Total Inventory Value
            </dt>
            <dd className="mt-1 text-3xl font-semibold text-gray-900">
              ₹{inventoryData.totalValue.toFixed(2)}
            </dd>
          </div>
        </div>

        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <dt className="text-sm font-medium text-gray-500 truncate">
              Total Items
            </dt>
            <dd className="mt-1 text-3xl font-semibold text-gray-900">
              {inventoryData.totalItems}
            </dd>
          </div>
        </div>

        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <dt className="text-sm font-medium text-gray-500 truncate">
              Low Stock Items
            </dt>
            <dd className="mt-1 text-3xl font-semibold text-gray-900">
              {inventoryData.lowStockItems.length}
            </dd>
          </div>
        </div>
      </div>

      <div className="bg-white p-6 shadow rounded-lg">
        <Bar data={chartData} options={chartOptions} />
      </div>

      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:px-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900">
            Low Stock Items
          </h3>
        </div>
        <div className="border-t border-gray-200">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Item
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Current Stock
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Value
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {inventoryData.lowStockItems.map((item) => (
                <tr key={item.id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {item.name}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {item.quantity} {item.weight_unit}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    ₹{(item.quantity * item.price_per_unit).toFixed(2)}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
